import React, { useState, useContext } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import {
  Toolbar,
  AppBar,
  IconButton,
  Box,
  Avatar,
  Button,
  useMediaQuery,
} from '@material-ui/core';
import SessionContext from '../../shared/contexts/session';

import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MenuIcon from '@material-ui/icons/Menu';
import User from './user';

import style from './style';

const useStyles = makeStyles(style);

const usersData = [
  {
    id: 1,
    name: 'Juan Peres',
    role: 'student',
    image: 'avatar.svg',
  },
  {
    id: 2,
    name: 'Maria Peres',
    role: 'student',
    image: 'maria.svg',
  },
  {
    id: 3,
    name: 'Roberto Peres',
    role: 'student',
    image: 'roberto.svg',
  },
  {
    id: 4,
    name: 'Diego Peres',
    role: 'admin',
    image: 'diego.svg',
  },
];

const TopBar = ({ setOpenMenu, openMenu }) => {
  const classes = useStyles();
  const theme = useTheme();
  const matchesXs = useMediaQuery(theme.breakpoints.only('xs'));

  const { isAuthenticated, setUserType } = useContext(SessionContext);

  const handleMenu = ev => {
    setOpenMenu(!openMenu);
  };

  const [users] = useState(usersData);

  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorES, setAnchorES] = useState(null);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setUserType('student');

    setAnchorEl(null);
  };

  const openLanguageMenu = event => {
    setAnchorES(event.currentTarget);
  };

  const handleLanguageClose = () => {
    setAnchorES(null);
  };

  return (
    <AppBar position="fixed" className={classes.appBar}>
      <Toolbar className={classes.toolbar}>
        {isAuthenticated && matchesXs && (
          <IconButton
            aria-label="open drawer"
            edge="start"
            className={classes.menuIcon}
            onClick={handleMenu}
          >
            <MenuIcon />
          </IconButton>
        )}

        <Link to="/">
          <img className={classes.logo} alt="Skyclick" src="/logo.svg" />
        </Link>
        <Box flexGrow={1} />
        {isAuthenticated && (
          <>
            <Button onClick={handleClick}>
              <Avatar
                alt="Avatar"
                src="./images/avatar/avatar.svg"
                className={classes.avatar}
              />
              <span className={classes.userLabel}>Juan Perez</span>
            </Button>
          </>
        )}
      </Toolbar>
    </AppBar>
  );
};

TopBar.propTypes = {
  setOpenMenu: PropTypes.func.isRequired,
  openMenu: PropTypes.bool.isRequired,
};

export default TopBar;
